import {MdLocalPizza as icon, MdSettingsInputComponent} from 'react-icons/md'
import PriceInput from '../components/PriceInput'

export default {
    //Computer name. Always singular/lowercase 
    name: 'pizza',

    //visible title
    title: 'Pizzas',
    type: 'document',
    icon,
    fields: [
        {
            name: 'name',
            title: 'Pizza Name',
            type: 'string',
            description: 'Name of the pizza',
        },

        {
            name: 'slug',
            title: 'slug',
            type: 'slug',
            options: {
                source: 'name',
                maxLength: 100,
            },
        },

        {
            name: 'image',
            title: 'Image',
            type: 'image',
            options: {
                hotspot: 'true',
            },
        },

        {
            name: 'price',
            title: 'Price',
            type: 'number',
            description: 'Price of the pizza in cents',
            
            //This is a function that includes the Rule object that contains helper methods
            validation: Rule=>Rule.min(1000).max(50000), //$10 min, $50 max

            //Adding custom input component
            inputComponent: PriceInput,

        },

        {   /*want a 1 to many relationship, so want an array of references. 
            creating an array of a type of reference; which references a type of topping.
            the reference to topping comes from the 'computer name' from topping.js. 
            This creates a relationship between the 2 schemas. In essence, creating an array 
            of references that references topping. 
        
            This DOES NOT create a related/reference the toppings schema => of: [{type: 'string'}], This creates field that allows me to type toppings. 
            */
            name: 'toppings',
            title: 'Toppings',
            type: 'array',
            of: [{type: 'reference', to:[{type: 'topping'}]}]
        },
    ],
    
    /*creating a custom preview to display toppings that appear under pizza name. preview property
    has select and prepare. the fields that I want to select are title, media and toppings. */
    preview: {
        select: {
            title: 'name',
            media: 'image',
            //toppings:'toppings' -> this will return the key and references id's. 
            toppings0: 'toppings.0.name',  //sanity studio requires this syntax. this is selecting the toppings array, grab the 1st element then the name
            toppings1: 'toppings.1.name',
            toppings2: 'toppings.2.name',
            toppings3: 'toppings.03.name',
        },

        /*Option1
        prepare: fields =>{
            console.log(fields);
            return 'pizza name';
        }
        */

        /*Option2
        prepare: ({title, media, toppings0, toppings1, toppings2, toppings3}) =>{
            console.log(title, media, toppings0, toppings1, toppings2, toppings3);
            return 'pizza name';
        }
        */

        /*using a rest parameter in the function to capture the remaining arguments into an argument. 
        this says anything else that I have not destructured, put it in an object called toppings  */
        prepare: ({title, media, ...toppings}) =>{
            //console.log(title, media, toppings);

            //filter out undefined toppings
            //const tops = Object.values(toppings).filter(topping => topping !== undefined)
            const tops = Object.values(toppings).filter(Boolean)
            //return the preview object for the pizza
            return {
                title,
                media,
                subtitle: Object.values(tops).join(['+']),
            }
        }


    }
};

